async function DELETE(request: AuthenticatedRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const prospectId = searchParams.get('prospectId')
    const mondayUserId = searchParams.get('mondayUserId')

    if (!prospectId || !mondayUserId) {
      return NextResponse.json(
        { success: false, error: 'prospectId and mondayUserId are required' },
        { status: 400 }
      )
    }

    const success = await prospectAssignmentRepo.removeAssignment(prospectId, mondayUserId)

    if (success) {
      return NextResponse.json({
        success: true,
        message: 'Assignment removed successfully'
      })
    } else {
      return NextResponse.json(
        { success: false, error: 'Assignment not found or could not be removed' },
        { status: 404 }
      )
    }
  } catch (error) {
    console.error('Error removing prospect assignment:', error)
    return NextResponse.json(
      { success: false, error: 'Failed to remove assignment' },
      { status: 500 }
    )
  }
}
