export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url)
    const inspectorId = searchParams.get('inspectorId')
    const limit = parseInt(searchParams.get('limit') || '5')

    if (!inspectorId) {
      return NextResponse.json({ error: 'inspectorId is required' }, { status: 400 })
    }

    // Fetch recent inspections ordered by updatedAt
    const inspections = await prisma.inspection.findMany({
      where: {
        mondayInspectorId: inspectorId,
      },
      orderBy: {
        updatedAt: 'desc',
      },
      take: limit,
      select: {
        id: true,
        address: true,
        streetAddress: true,
        city: true,
        zipCode: true,
        photoUrls: true,
        videoUrls: true,
        status: true,
        currentStep: true,
        isUrgent: true,
        createdAt: true,
        updatedAt: true,
      },
    })

    // Process inspections to add photo/video counts and relative time
    const processedInspections = inspections.map(inspection => {
      // Count photos and videos from JSON strings or plain URLs
      let photoCount = 0
      let videoCount = 0

      inspection.photoUrls.forEach(entry => {
        try {
          JSON.parse(entry) // If it's a JSON string, it's valid
          photoCount++
        } catch {
          if (entry.trim()) photoCount++ // If it's a plain URL and not empty
        }
      })

      inspection.videoUrls.forEach(entry => {
        try {
          JSON.parse(entry) // If it's a JSON string, it's valid
          videoCount++
        } catch {
          if (entry.trim()) videoCount++ // If it's a plain URL and not empty
        }
      })

      // Calculate relative time
      const now = new Date()
      const updatedAt = new Date(inspection.updatedAt)
      const diffMs = now.getTime() - updatedAt.getTime()
      const diffHours = Math.floor(diffMs / (1000 * 60 * 60))
      const diffMinutes = Math.floor(diffMs / (1000 * 60))

      let timeAgo = ''
      if (diffHours >= 24) {
        const diffDays = Math.floor(diffHours / 24)
        timeAgo = `${diffDays} day${diffDays > 1 ? 's' : ''} ago`
      } else if (diffHours >= 1) {
        timeAgo = `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`
      } else if (diffMinutes >= 1) {
        timeAgo = `${diffMinutes} minute${diffMinutes > 1 ? 's' : ''} ago`
      } else {
        timeAgo = 'Just now'
      }

      return {
        ...inspection,
        photoCount,
        videoCount,
        timeAgo,
      }
    })

    return NextResponse.json({
      inspections: processedInspections,
    })
  } catch (error) {
    console.error('Get recent inspections error:', error)
    return NextResponse.json(
      { error: 'Failed to get recent inspections' },
      { status: 500 }
    )
  }
}
