export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  const { id } = await params;

  if (!id) {
    return NextResponse.json({ error: 'Prospect ID is required' }, { status: 400 });
  }

  try {
    const response = await fetch(`https://storm-track-rm.vercel.app/api/prospect/${id}?includeAll=true`, {
      headers: {
        'x-api-token': 'storm-track-default-token-2024',
        'Content-Type': 'application/json',
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        'Pragma': 'no-cache',
        'Expires': '0',
      },
    });

    if (!response.ok) {
      throw new Error(`Storm Track API responded with status: ${response.status}`);
    }

    const data = await response.json();

    return NextResponse.json({
      success: true,
      data: data
    });

  } catch (error) {
    console.error('Error fetching prospect properties:', error);
    return NextResponse.json(
      { 
        error: 'Failed to fetch prospect properties',
        details: error instanceof Error ? error.message : 'Unknown error'
      }, 
      { status: 500 }
    );
  }
}
