export async function POST(request: NextRequest) {
  try {
    const data = await request.json()

    // Map inspectorId to mondayInspectorId for backward compatibility
    const createData: CreateInspectionData = {
      ...data,
      mondayInspectorId: data.mondayInspectorId || data.inspectorId
    }

    const inspection = await inspectionRepo.createInspection(createData)

    return NextResponse.json(inspection)
  } catch (error) {
    console.error('Create inspection error:', error)
    return NextResponse.json(
      { error: 'Failed to create inspection' },
      { status: 500 }
    )
  }
}
